import axios from 'axios';
import React, { Component } from 'react';
import { Text, View, ImageBackground, StyleSheet, Alert } from 'react-native';
import MapView, { Marker } from 'react-native-maps';

export default class IssLocationScreen extends Component {

    constructor(props) {
        super(props);
        this.state = {
            ISS_LOCATION:{}
        }
    }

    componentDidMount() {
        this.get_DATA()
    }

    get_DATA = () => {
        axios.get("https://api.wheretheiss.at/v1/satellites/25544").then(Response_get => {
            this.setState({ ISS_LOCATION: Response_get.data })
        })
            .catch(
                error => {
                    Alert.alert("error", error.message)
                }
            )
    }

    render() {
        return (
            <View style={{ flex: 1, }}>
                <ImageBackground source={require("../assets/bg.png")} style={styles.bg}>
                    <Text style={styles.txt}>International Space Station Location</Text>
                    <MapView
                        region={{
                            latitude: this.state.ISS_LOCATION.latitude,
                            longitude: this.state.ISS_LOCATION.longitude,
                            latitudeDelta: 100,
                            longitudeDelta: 100,
                        }}
                        style={{width: '100%', height: '100%'}}
                    />
                </ImageBackground>
            </View>
        )
    }
}



const styles = StyleSheet.create({
    bg: {
        flex: 1,
        resizeMode: "cover",
    },
    txt: {

        color: "white",
        textAlign: "left",
        fontSize: 40,
        marginTop: 70,
        marginLeft: 10,
        fontWeight: "bold",
        textShadowOffset: { width: 0.5, height: 0.5 },
        textShadowRadius: 2,
        textShadowColor: 'grey',
    },
    txt1: {
        color: "white",
        right: 20,
        bottom: -35,
        position: "absolute",
        fontSize: 80,
        marginTop: 70,
        marginLeft: 0,
        fontWeight: "bold",
        opacity: 0.5,
        textShadowOffset: { width: 2, height: 2 },
        textShadowRadius: 10,
        textShadowColor: 'black',
    },
    txt2: {
        color: "white",
        textAlign: "left",
        fontSize: 40,
        marginTop: 70,
        marginLeft: 30,
        marginBottom: 15,
        fontWeight: "bold",
        textShadowOffset: { width: 2, height: 2 },
        textShadowRadius: 10,
        textShadowColor: 'black',
    },
    TOuchOPA: {
        backgroundColor: "#282424",
        margin: 20,
        marginTop: 100,
        borderRadius: 30,
    }
});
